import { useEffect, useState } from "react";
import Loading from "../Components/Loading";
import ItemTable from "../Components/ItemTable";
import { useParams } from "react-router-dom";

const fetchItems = () => {
    return fetch(`/items/`).then((res) => res.json());
};
const deleteItem = (id) => {
    return fetch(`/items/${id}`, { method: "DELETE" }).then((res) =>
        res.json()
    );
};

const ItemList = () => {
    const {sortBy, sortOrder} = useParams();
    const [loading, setLoading] = useState(true);
    const [items, setItems] = useState(null);
    const handleDelete = (id) => {
        deleteItem(id);

        setItems((items) => {
            return items.filter((item) => item.id !== id);
        });
    };

    useEffect(() => {
        fetchItems()
            .then((items) => {
                console.log(items);
                setLoading(false);
                // if (sortBy && sortOrder) {
                //     items = items.sort((a, b) => {
                //         const toSortA = a[sortBy].toUpperCase();
                //         const toSortB = b[sortBy].toUpperCase();
                //         if (toSortA < toSortB) {
                //             return sortOrder === "asc" ? -1 : 1;
                //         }
                //         if (toSortA > toSortB) {
                //             return sortOrder === "asc" ? 1 : -1;
                //         }
                //         return 0;
                //     });
                // }
                 console.log(items);
                setItems(items);
            })
    }, []);
//sortBy, sortOrder
    if (loading) {
        return <Loading />;
    }

    return <ItemTable items={items} onDelete={handleDelete} />;
};

export default ItemList;
